const header = document.querySelector(".header");
const hamburger_toggle = document.querySelector(".humberger-toggle");
const sidebar = document.querySelector('#sidebar')
const sidebar_child = [...document.querySelector("#sidebar .sidebar-content-container").children];





// console.log(sidebar)
// console.log(sidebar_child)
window.addEventListener("click",(e)=>{
  switch (e.target ) {
    case header:
      window.location = 'index.php'; 
      break;
    case hamburger_toggle && hamburger_toggle.firstChild:
      sidebar.classList.toggle("close");
      sidebar_child.map((item)=>{
        item.lastElementChild.classList.toggle("nav-child-closed");
      
      })
      break;
    default:
      break;
  }
})
